files.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222.2 84% 4.9%;
    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 222.2 84% 4.9%;
    --primary: 160 84% 39%;
    --primary-foreground: 210 40% 98%;
    --secondary: 210 40% 96.1%;
    --secondary-foreground: 222.2 47.4% 11.2%;
    --muted: 210 40% 96.1%;
    --muted-foreground: 215.4 16.3% 46.9%;
    --accent: 210 40% 96.1%;
    --accent-foreground: 222.2 47.4% 11.2%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;
    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 160 84% 39%;
    --radius: 0.5rem;

    /* Blinkist specific colors */
    --midnight: 201.37 94.81% 15.10%;
    --green: 160 84% 39%;
    --blue: 214.29 100% 50%;
    --search-bg: 150 20% 96%;
    --search-text: 197 4% 58%;
    --sidebar-bg: 240 100% 98%;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 160 84% 39%;
    --primary-foreground: 222.2 47.4% 11.2%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 160 84% 39%;
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    font-family: "CeraPRO", sans-serif;
    -webkit-font-smoothing: antialiased;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    @apply text-midnight;
  }

  /* Custom placeholder color */
  ::placeholder {
    color: rgb(142, 150, 154);
    opacity: 1;
  }
}

/* CeraPRO Font */
@font-face {
  font-display: swap;
  font-family: "CeraPRO";
  font-weight: 700;
  src: url("https://static-cdn.blinkist.com/webfonts/323ED8_0_0.woff2") format("woff2"),
    url("https://static-cdn.blinkist.com/webfonts/323ED8_0_0.woff") format("woff");
}

@font-face {
  font-display: swap;
  font-family: "CeraPRO";
  font-weight: 500;
  src: url("https://static-cdn.blinkist.com/webfonts/323ED8_1_0.woff2") format("woff2"),
    url("https://static-cdn.blinkist.com/webfonts/323ED8_1_0.woff") format("woff");
}

@font-face {
  font-display: swap;
  font-family: "CeraPRO";
  font-style: italic;
  font-weight: 400;
  src: url("https://static-cdn.blinkist.com/webfonts/323ED8_2_0.woff2") format("woff2"),
    url("https://static-cdn.blinkist.com/webfonts/323ED8_2_0.woff") format("woff");
}

@font-face {
  font-display: swap;
  font-family: "CeraPRO";
  font-weight: 400;
  src: url("https://static-cdn.blinkist.com/webfonts/323ED8_3_0.woff2") format("woff2"),
    url("https://static-cdn.blinkist.com/webfonts/323ED8_3_0.woff") format("woff");
}

/* Blinkist specific utility classes */
.text-midnight {
  color: hsl(var(--midnight));
}

.bg-green {
  background-color: hsl(var(--green));
}

.bg-sidebar {
  background-color: hsl(var(--sidebar-bg));
}
